INSERT INTO Post (user_id, create_date, content) VALUES(4,'2022-07-03', 'Ta wyprawa motocyklowa była inna niż pozostałe. Zakończyła się gdy właściwie się rozpoczynała. Przejechaliśmy bezpiecznie Ukrainę. W trzy dni przepłynęliśmy promem Morze Czarne mijając Krym. Przeżyliśmy piękny wschód słońca w Batumi.');
